/**
 * Detect Electron renderer process, which is node, but we should
 * treat as a browser.
 */

if (typeof process !== 'undefined' && process.type === 'renderer') {
  module.exports = require('socket.io/node_modules/debug/src/browser.js');
} else {
  module.exports = require('socket.io/node_modules/debug/src/node.js');
}
